package Arrays;

public class Validate_IP_Address_468 {
  static String Neither = "Neither";


  public static void main(String[] args) {
    System.err.println(validIPAddress("..."));

  }

  public static String validIPAddress(String queryIP) {

    String[] ip4SplitArray = queryIP.split("\\.");
    String[] ip6SplitArray = queryIP.split("\\:");
    if (queryIP.charAt(0) == ':' || queryIP.charAt(queryIP.length() - 1) == ':')
      return Neither;

    if (ip4SplitArray != null && ip4SplitArray.length == 4) {
      for (String ip4Split : ip4SplitArray) {
        if (!validateIp4String(ip4Split))
          return Neither;
      }
      return "IPv4";
    } else if (ip6SplitArray != null && ip6SplitArray.length == 8) {
      for (String ip6Split : ip6SplitArray) {
        if (!validateIp6String(ip6Split))
          return Neither;
      }
      return "IPv6";
    }

    return Neither;
  }

  private static boolean validateIp6String(String ip6Split) {
    if (ip6Split == null || "".equals(ip6Split.trim()) || (ip6Split.length() > 4))
      return false;

    char[] chars = ip6Split.toCharArray();
    for (char c : chars) {
      if (!((c >= 'a' && c <= 'f') || (c >= 'A' && c <= 'F') || (c >= '0' && c <= '9'))) return false;

    }

    return true;
  }

  private static boolean validateIp4String(String ip4Split) {

    if (ip4Split == null || "".equals(ip4Split.trim()) || (ip4Split.startsWith("0") && ip4Split.length() > 1) || ip4Split.length() > 3)
    return false;

    char[] chars = ip4Split.toCharArray();
    for (char c : chars)
      if (c < '0' || c > '9') return false;

    return (0 <= Integer.valueOf(ip4Split) && Integer.valueOf(ip4Split) <= 255);
  }
}
