package Strings;

import java.util.HashMap;
import java.util.Map;

public class Permutation_in_String_567 {

  public static void main(String[] args) {
    System.err.println(checkInclusion(s1, s2));

    //System.err.println(checkInclusion("adc", "dcda"));
    //System.err.println(checkInclusion("ab", "eidbaooo"));
    //System.err.println(checkInclusion("ab", "eidboaoo"));

  }

  public static boolean checkInclusion(String s1, String s2) {
    Map<Character, Integer> s1_map = new HashMap();
    for (int i = 0; i < s1.length(); i++) {
      char s1_char = s1.charAt(i);
      s1_map.put(s1_char, s1_map.getOrDefault(s1_char, 0) + 1);
    }

    int targetCount = s1.length();
    int matching_chars_starting_index = -1;
    Map<Character, Integer> s2_matched_count_map = new HashMap();
    char[] s2_chars = s2.toCharArray();
    for (int i = 0; i < s2_chars.length; i++) {
      char ch = s2_chars[i];
      System.err.println("ch=" + ch);
      if (s1_map.containsKey(ch)) {
        // add current s2 char to s2_map.
        if (matching_chars_starting_index == -1) matching_chars_starting_index = i;
        s2_matched_count_map.put(ch, s2_matched_count_map.getOrDefault(ch, 0) + 1);
        if (s1_map.get(ch) <= s2_matched_count_map.get(ch)) {
          int matchingWindowLength = i - matching_chars_starting_index + 1;
          if (matchingWindowLength == targetCount)
            return true;

        } else if (s1_map.get(ch) < s2_matched_count_map.get(ch)) {
          // i.e. this combination/window has become invalid now.
          //so start removing character (i.e. current s2_char i.e. ch)  from front of the window.
          while (true) {
            char left_index_char = s2_chars[matching_chars_starting_index];
            System.err.println("left_index_char=" + left_index_char);
            s2_matched_count_map.put(left_index_char, s2_matched_count_map.getOrDefault(left_index_char, 1) - 1);
            matching_chars_starting_index++;
            if (left_index_char == ch) break;
          }
        }
      } else {
        s2_matched_count_map.clear();
        matching_chars_starting_index = -1;
      }
    }
    return false;
  }

  static String s1 = "";
  static String s2 = "";

}
